import Users from "./Components/Card";
import './App.css';

import React, { Component } from 'react'
	
class App extends Component {
  constructor(props){
	super(props)
		
	// Set initial state
	this.state = {users_data :[],
                loading : false              
  }
  

	this.updateState = this.updateState.bind(this)
  }
    
  updateState(){
    sleep = (milliseconds) => {
      return new Promise(resolve => setTimeout(resolve, milliseconds))
  }
      // document.getElementById("main").style.display='flex';
     this.setState({loading : true})
      const link="https://reqres.in/api/users?page=1";
      fetch(link)
      sleep(5000).then(response => response.json())
      .then((users) => {
        
        this.setState({users_data :users.data,
                        loading:false
        })
        console.log(users.data);
      })
      .catch((error) => {
        console.error(error)
      })
  }
    
  render(){
    return (<>
      <nav>
          <div className="box">
            <div className="row">
            <div className="column1">
            <h2>LetsGrowMore</h2>
            </div>
            <div className="column2">
            <button onClick={this.updateState} loading={this.state.loading === true} >Get Users</button>
            </div>
            </div>
          </div>
        </nav>
        <div className="box2">
         <Users loading={this.state.loading } users={this.state.users_data}/>
         </div>
    </>
    )
  }
}
	
export default App;
