import Users from "./Components/Card";
import './App.css';

import React, { Component } from 'react'
	
class App extends Component {
  constructor(props){
	super(props)
		
	// Set initial state
	this.state = {users_data :[],
                loading : false              
  }
  

	this.updateState = this.updateState.bind(this)
  }
  //   componentDidMount() {
  //   this.updateState()
  //   setInterval(this.updateState, 10000);
  // }
  updateState(){
  
      // document.getElementById("main").style.display='flex';
     this.setState({loading : true})
     
      const link="https://reqres.in/api/users?page=1";
      setTimeout(() => {
        
        fetch(link)
        .then(response => response.json())
        .then((users) => {
          
          this.setState({users_data :users.data,
                          loading:false
          })
        })
        .catch((error) => {
          console.error(error)
        })
      }, 800);
  }

    
  render(){
    return (<>
      <nav>
          <div className="box">
            <div className="row">
            <div className="column1">
            <h2>shadman | AdMe</h2>
            </div>
            <div className="column2">
            <button onClick={this.updateState} loading={this.state.loading === true} >Get Users</button>
            </div>
            </div>
          </div>
        </nav>
        <div className="box2">
         <Users loading={this.state.loading } users={this.state.users_data}/>
         </div>
    </>
    )
  }
}
	
export default App;
